ord(auth, email, password);","                // Salva o nome no Firestore ao criar a conta","                await setDoc(doc(db, 'users', userCredential.user.uid), {","                    name,","                    email,","                    createdAt: new Date(),","                });","            } else {","                userCredential = await signInWithEmailAndPassword(auth, email, password);","            }","            setUser({ uid: userCredential.user.uid, name: name || userCredential.user.email.split('@')[0] });","            setToast({ message: isSignUp ? 'Conta criada com sucesso!' : 'Login realizado com sucesso!', type: 'success' });","        } catch (err) {","            handleActionError(err, 'Falha na autenticação. Verifique e-mail/senha.');","        } finally {","            setIsLoading(false);","        }","    };","","    const handleLogout = async () =\u003e {","        try {","            await signOut(auth);","            setRoomData(null);","            setRoomPlayers([]);","            setToast({ message: 'Logout realizado.', type: 'info' });","        } catch (err) {","            handleActionError(err, 'Falha ao sair.');","        }","    };","","    // --- LÓGICA DE SALA ---","","    const isHost = useMemo(() =\u003e roomData \u0026\u0026 userId === roomData.hostId, [roomData, userId]);","    const myPlayer = useMemo(() =\u003e roomPlayers.find(p =\u003e p.userId === userId), [roomPlayers, userId]);","","    // Hook para sincronizar dados da sala em tempo real","    useEffect(() =\u003e {","        if (!roomId || view !== 'room') return;","","        const roomRef = doc(db, 'rooms', roomId);","        const playersRef = collection(db, 'rooms', roomId, 'players');","","        // Sincroniza dados da sala","        const unsubscribeRoom = onSnapshot(roomRef, (docSnap) =\u003e {","            if (docSnap.exists()) {","                const data = docSnap.data();","                setRoomData(data);","                // Atualiza a pergunta ativa","                if (data.currentQuestionIndex !== -1 \u0026\u0026 data.status !== 'FINISHED') {","                    const q = doc(db, 'rooms', roomId, 'questions', data.currentQuestionIndex.toString());","                    onSnapshot(q, (qSnap) =\u003e {","                        if (qSnap.exists()) setActiveQuestion(qSnap.data());","                    });","                } else {","                    setActiveQuestion(null);","                }","            } else {","                setRoomData(null);","                setToast({ message: 'Sala excluída ou você foi removido.', type: 'info' });","                setView('menu');","            }","        });","","        // Sincroniza jogadores","        const unsubscribePlayers = onSnapshot(playersRef, (snapshot) =\u003e {","            setRoomPlayers(snapshot.docs.map(doc =\u003e ({ id: doc.id, ...doc.data() })));","        });","","        return () =\u003e {","            unsubscribeRoom();","            unsubscribePlayers();","        };","    }, [roomId, view]);","","    // Função para entrar na sala","    const joinRoom = useCallback(async (id, passcode) =\u003e {","        if (!user) return;","        setIsLoading(true);","        setError(null);","","        try {","            const roomRef = doc(db, 'rooms', id);","            const roomSnap = await getDocs(roomRef);","","            if (!roomSnap.exists()) {","                throw new Error('Sala não encontrada.');","            }","","            const data = roomSnap.data();","","            if (data.passcode !== passcode) {","                throw new Error('Senha incorreta.');","            }","","            if (data.playerCount \u003e= MAX_PLAYERS) {","                throw new Error('Sala lotada.');","            }","            if (data.status !== 'LOBBY' \u0026\u0026 data.status !== 'FINISHED') {","                 throw new Error('O jogo já começou nesta sala.');","            }","","            const playerRef = doc(db, 'rooms', id, 'players', user.uid);","            await setDoc(playerRef, {","                userId: user.uid,","                name: user.name,","                score: 0,","                isHost: data.hostId === user.uid,","                hasResponded: false,","                hasGuessed: false,","            });","","            await updateDoc(roomRef, {","                playerCount: data.playerCount + 1","            });","","            setRoomId(id);","            setView('room');","            setToast({ message: `Você entrou na sala ${id}!`, type: 'success' });","","        } catch (err) {","            handleActionError(err, err.message);","        } finally {","            setIsLoading(false);","        }","    }, [user, handleActionError]);","","","    // Função para criar a sala","    const createRoom = async () =\u003e {","        if (!user) return;","        setIsLoading(true);","        setError(null);","","        try {","            const id = generateRandomId();","            const passcode = generateRandomId();","            const roomRef = doc(db, 'rooms', id);","","            // Seleciona 10 perguntas aleatórias","            const selectedQuestions = shuffleArray(PERGUNTAS_PADRAO).slice(0, MAX_ROUNDS);","            ","            await setDoc(roomRef, {","                id,","                passcode,","                hostId: user.uid,","                playerCount: 1,","                status: 'LOBBY', // LOBBY, Q_WAITING, A_COLLECTING, A_REVIEW, G_COLLECTING, G_SCORING, FINISHED","                currentQuestionIndex: -1,","                totalRounds: MAX_ROUNDS,","                questions: selectedQuestions,","                createdAt: new Date(),","            });","","            // Adiciona o host como jogador","            const playerRef = doc(db, 'rooms', id, 'players', user.uid);","            await setDoc(playerRef, {","                userId: user.uid,","                name: user.name,","                score: 0,"," 